    #[doc = ""]
    #[doc = " \\since This function is available since SDL 2.0.0."]
    #[doc = ""]
    #[doc = " \\sa SDL_CloseAudioDevice"]
    #[doc = " \\sa SDL_GetAudioDeviceName"]
    #[doc = " \\sa SDL_LockAudioDevice"]
